from django import forms# from .models import Nhanvienfrom django.contrib import adminfrom qlns.models import *class AddnhanvienForm(forms.ModelForm):    class Meta:        model = Nhanvien        fields ='__all__'        exclue = 'id'        widgets = {            'cmnd': forms.TextInput(attrs={'class': 'form-control', 'name': 'cmnd', 'pattern': '[0-9]{1,12}',                                           'title': 'Cmnd là so có 12 chữ số', 'id': 'cmnd'}),        }class AddnhanvienAdmin(admin.ModelAdmin):    exclude = 'tennv'    form = AddnhanvienFormclass Changeformnhanvien(forms.ModelForm):    class Meta:        model = Nhanvien        fields ='__all__'        widgets ={            'manv':forms.TextInput(attrs={'class':'form-control','name':'manv','id':'manvs'}),            'tennv': forms.TextInput(attrs={'class': 'form-control', 'name': 'tennv', 'id': 'tennv'}),            'ngaysinh': forms.DateInput(attrs={'class': 'form-control', 'name': 'ngaysinh', 'id': 'ngaysinh'}),            'diachi': forms.TextInput(attrs={'class': 'form-control', 'name': 'diachi', 'id': 'diachi'}),            'quequan': forms.TextInput(attrs={'class': 'form-control', 'name': 'quequan', 'id': 'quequan'}),            'cmnd': forms.TextInput(attrs={'class': 'form-control', 'name': 'cmnd','pattern':'[0-9]{1,12}' ,'title':'Cmnd là so có 12 chữ số','id': 'cmnd'}),            'line': forms.TextInput(attrs={'class': 'form-control', 'name': 'line', 'id': 'line'}),            'email': forms.EmailInput(attrs={'class': 'form-control', 'name': 'email', 'id': 'email'}),            'sdt': forms.TextInput(attrs={'class': 'form-control', 'name': 'sdt', 'id': 'sdt'}),        }class Deleteformnhanvien(forms.ModelForm):    class Meta:        model = Nhanvien        fields ='__all__'